CREATE OR REPLACE FUNCTION public.create_token( client_master_token character varying, access_token character varying, service character varying, expires_in integer) RETURNS tokens LANGUAGE 'sql'
COST 100 VOLATILE SECURITY DEFINER
AS $BODY$ INSERT INTO tokens (client_master_token, id, service, expires_in) VALUES ($1, $2, $3, $4) RETURNING *; $BODY$;
