changeSet.sql:
CREATE OR REPLACE FUNCTION public.delete_consent( id text) RETURNS TABLE(id text, state character varying, access text, recurring_indicator boolean, last_action timestamp without time zone, valid_until timestamp without time zone, last_updated timestamp without time zone, frequency_per_day integer, combined_service_indicator boolean, chosen_sca_method character varying, psu_id character varying, psu_id_type character varying, psu_ip_address character varying, psu_ip_port integer, psu_user_agent text, psu_geo_location text, psu_corporate_id character varying, psu_corporate_id_type character varying, x_request_id uuid, created_at timestamp without time zone) LANGUAGE 'sql'
COST 100 VOLATILE SECURITY DEFINER ROWS 1000
AS $BODY$ DELETE FROM consents WHERE consents.id = $1 RETURNING id, (SELECT name FROM consent_states WHERE consent_states.id = state) as state, access, recurring_indicator, last_action, valid_until, last_updated, frequency_per_day, combined_service_indicator, (SELECT name FROM sca_methods WHERE sca_methods.id = chosen_sca_method) as chosen_sca_method, psu_id, psu_id_type, psu_ip_address, psu_ip_port, psu_user_agent, psu_geo_location, psu_corporate_id, psu_corporate_id_type, x_request_id, created_at; $BODY$;
