changeSet.sql:
CREATE OR REPLACE FUNCTION public.get_interrupted_tasks( ) RETURNS TABLE(id uuid, signature text, goal text, worker_type character varying, class text) LANGUAGE 'sql'
COST 100 VOLATILE SECURITY DEFINER ROWS 1000
AS $BODY$ select id, signature, goal, worker_type, class from tasks where queued = false AND done = false AND done_at IS NULL AND final_failure_text IS NULL AND final_failure_code IS NULL; $BODY$;
